def smallest_distance_pair(nums, k)
  dist = []
  for i in 0...nums.length
    for j in (i+1)...nums.length
      dist << (nums[i] - nums[j]).abs 
    end
  end
  
  dist = dist.sort
  return (dist[k-1] == nil) ? 0 : dist[k-1]
end

